export interface ICD10Suggestion {
  code: string;
  description: string;
  confidence: number;
  category: string;
  matchedKeywords: string[];
}

export interface ICD10Service {
  suggestCodes: (clinicalText: string, limit?: number) => ICD10Suggestion[];
  getCodeDetails: (code: string) => { description: string; keywords: string[]; category: string } | null;
  searchCodes: (query: string) => ICD10Suggestion[];
}

class ICD10ServiceImpl implements ICD10Service {
  private codeIndex: Map<string, { description: string; keywords: string[]; category: string }>;

  constructor() {
    this.codeIndex = new Map();

    // Build the index
    for (const [category, codes] of Object.entries(ICD10_CODES)) {
      for (const [code, data] of Object.entries(codes)) {
        this.codeIndex.set(code, {
          description: data.description,
          keywords: data.keywords,
          category,
        });
      }
    }
  }

  suggestCodes(clinicalText: string, limit: number = 10): ICD10Suggestion[] {
    const text = clinicalText.toLowerCase();
    const suggestions: ICD10Suggestion[] = [];

    for (const [code, data] of Array.from(this.codeIndex.entries())) {
      let confidence = 0;
      const matchedKeywords: string[] = [];

      // Check for exact keyword matches
      for (const keyword of data.keywords) {
        if (text.includes(keyword.toLowerCase())) {
          confidence += 0.8; // High confidence for exact keyword match
          matchedKeywords.push(keyword);
        } else {
          // Check for partial matches
          const words = keyword.toLowerCase().split(' ');
          let partialMatches = 0;
          for (const word of words) {
            if (text.includes(word)) {
              partialMatches++;
            }
          }
          if (partialMatches > 0) {
            confidence += (partialMatches / words.length) * 0.4; // Lower confidence for partial matches
            if (partialMatches === words.length) {
              matchedKeywords.push(keyword);
            }
          }
        }
      }

      // Boost confidence for multiple matches
      if (matchedKeywords.length > 1) {
        confidence *= 1.2;
      }

      // Cap confidence at 1.0
      confidence = Math.min(confidence, 1.0);

      if (confidence > 0.1) { // Only include suggestions with reasonable confidence
        suggestions.push({
          code,
          description: data.description,
          confidence,
          category: data.category,
          matchedKeywords,
        });
      }
    }

    // Sort by confidence (highest first) and return top results
    return suggestions
      .sort((a, b) => b.confidence - a.confidence)
      .slice(0, limit);
  }

  getCodeDetails(code: string): { description: string; keywords: string[]; category: string } | null {
    return this.codeIndex.get(code) || null;
  }

  searchCodes(query: string): ICD10Suggestion[] {
    const lowerQuery = query.toLowerCase();
    const results: ICD10Suggestion[] = [];

    for (const [code, data] of Array.from(this.codeIndex.entries())) {
      let relevance = 0;

      // Check code match
      if (code.toLowerCase().includes(lowerQuery)) {
        relevance += 1.0;
      }

      // Check description match
      if (data.description.toLowerCase().includes(lowerQuery)) {
        relevance += 0.8;
      }

      // Check keyword matches
      for (const keyword of data.keywords) {
        if (keyword.toLowerCase().includes(lowerQuery)) {
          relevance += 0.6;
        }
      }

      if (relevance > 0) {
        results.push({
          code,
          description: data.description,
          confidence: Math.min(relevance, 1.0),
          category: data.category,
          matchedKeywords: data.keywords.filter(k => k.toLowerCase().includes(lowerQuery)),
        });
      }
    }

    return results.sort((a, b) => b.confidence - a.confidence).slice(0, 20);
  }
}

// Singleton instance
const icd10Service = new ICD10ServiceImpl();

export const useICD10Service = (): ICD10Service => {
  return useMemo(() => icd10Service, []);
};